import  {ADD_FAV, DELETE_FAV} from './action-types';
const initialState={myFavorites:[]};

const reducer = (state=initialState, actions) =>{
    switch(actions.type){
    case ADD_FAV:
        return {...state, myFavorites: [...state.myFavorites, actions.payload]}
    case DELETE_FAV:
        return {
            ...state,
            myFavorites: state.myFavorites.filter(char=> char.id !== actions.payload)
        }
    default:
        return {...state}
    }}
    export default reducer;
