import styled from 'styled-components';
import {Link} from 'react-router-dom';
const Garish = styled.button`
border: 4px solid black`;

const Nombres = styled.h2`
color: brown;
border: 3px solid goldenrod;
border-style: inset;
`
export default function Card({char, onClose}) {
   
   return (
      <div>
          <Garish onClick={()=>onClose(char.id)} key={char.id}>X</Garish>
         <Link to={`/detail/${char.id}`}><Nombres className='cafe'>{char.name}</Nombres></Link>
         <h2>{char.species}</h2>
         <h2>{char.gender}</h2>
         <img  src={char.image} alt="No pos... No lo sÃ© Rick, parece falso" /> 
      </div>
   );
}
