import axios from "axios";
import { ADD_FAV, DELETE_FAV, ORDER, FILTER } from "./action-types";

export const addF = async(charact)=>{
    try{ return {type: ADD_FAV, payload:await axios.post('http://localhost:3001/rickandmorty/fav', charact)
    .then(result=> console.log('Okidoki'))}}
    catch(error){
        console.log(error.message);
    }
   
}
