import axios from "axios";
import { ADD_FAV, DELETE_FAV, ORDER, FILTER } from "./action-types";

export const addF = (charact)=>{
    return {type: ADD_FAV, payload: axios.post('http://localhost:3001/rickandmorty/fav', charact)
    .then(result=> console.log('Okidoki'))}
}
