import './App.css'
import Cards from './components/Cards.jsx'
import { useState } from 'react';
import Nav from './components/Nav.jsx';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
function App () {
  const [characters, setCharact] = useState([])
  const onSearch = (character)=>{
    const URL = "https://be-a-rym.up.railway.app/api"
    const KEY = '0cf7b1dc4eae.76519a01498f54d82cde'
    fetch(`${URL}/character/${character}?key=${KEY}`)
    .then((response) => response.json())
    .then((data) => {
       if (data.name) {
          setCharact((oldChars) => [...oldChars, data]);
       } else {
          window.alert('No hay personajes con ese ID');
       }
    });
  }
  const onClose= (id)=>{
    console.log(id);
    setCharact(characters.filter(charact=> charact.id!== id))
        }
  return (
    <div className='App' style={{ padding: '25px' }}>
      <hr />
      <div><Nav onSearch={onSearch}></Nav></div>
      <Routes>
        <Route path='/home' element={<div>
        <Cards
        onClose={onClose} characters={characters}/>
      </div>}></Route>
      </Routes>
      
      <hr />
     
    </div>
  )
}

export default App
