import Card from './Card';
import styled from 'styled-components';

const Patuta = styled.div`color: brown;
border: 3px solid goldenrod;
border-style: inset;
display: flex;
flex-direction: row;
justify-content: space-between;
margin: auto;
padding: 2 em;`
export default function Cards({characters}) {
   return (<Patuta>
      {characters.map(char=>{
         return <Card
         name= {char.name}
         species={char.species}
         gender= {char.gender}
         image={char.image}
         onClose={()=>window.alert('Emulando el cierre')}></Card>
      })
   }
   </Patuta>
)
}
