 import styled from 'styled-components';
 import {useState} from 'react';
 import validation from './validation'
 const Patata = styled.form`
 background-color: goldenrod;
 display: flex;
 flex-direction: column;`;
 const LetmyIn = styled.input` width: 25%;
 display: flex;
 flex-direction: row;
 margin: auto;`
 const Form = ()=>{
    const [userData, setUserData]= useState({username:'', password:''});
    const [errors, setErrors]=useState();
    const handlerInputChange = (event)=>{
        const property = event.target.name;
        const value = event.target.value;
        setUserData({...userData, [property]: value});
        validation(userData, setErrors, errors);
    }
    return(
        <Patata>
            <label htmlFor="username">username:</label>
                <LetmyIn type='text' name='username' onChange={handlerInputChange} value={userData.username}/> 

                <label htmlFor="password">password:</label>
                <input type='password' name='password' value={userData.pasword}/>   
                <button type='submit'></button>         
        </Patata>
    )
}
export default Form
